var advantagesList = document.querySelector(".advantages__list");

function getScrollbarWidth(element) {
    return window.innerWidth - document.documentElement.clientWidth;;
}
var bodd = document.body;
var scrollBarWidth = getScrollbarWidth(bodd);
console.log(scrollBarWidth)
if (advantagesList) {
    var runAnimtion = function () {
        for (var e = 0; e < bigNumbers.length; e++) anime.timeline().add({
            targets: bigNumbers[e],
            innerHTML: ["", bigNumbers[e].dataset.calc],
            easing: "linear",
            autoplay: !0,
            round: 1,
            duration: 2e3
        }).add({targets: bigNumbers[e].nextElementSibling, opacity: [0, 1], easing: "linear"})
    }, observer = new IntersectionObserver(function (e) {
        for (var t = 0; t < e.length; t++) e[t].intersectionRatio >= .1 && (runAnimtion(), observer.disconnect())
    }, {threshold: .1});
    observer.observe(advantagesList);
    var bigNumbers = document.querySelectorAll(".advantages__numbers")
}
var btnMenu = document.querySelector(".header__menu-btn"), menu = document.querySelector(".nav-list");
